import React, { useState } from 'react';
// import '../Style/Employee.css';
import "../Style/EmployeeForm.css"

const initialState = {
    name: '',
    department: '',
    experience: '',
    startDate: '',
    manager: '',
    skills: '',
    remarks: ''
};
const AddEdit = () => {
